sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   jslog(g_form.getValue('u_vendor_type'));
   var getBusinessDetails = new GlideAjax('GetCompanyByABN');

   getBusinessDetails.addParam('sysparm_name', 'getCompanyDetails');

   getBusinessDetails.addParam('sysparam_ABN', g_form.getValue('u_abn_number'));

   getBusinessDetails.getXML(populateBusinessNameField);

   function populateBusinessNameField(response){
	   try{
		   var businessFromScriptInclude =  response.responseXML.documentElement.getAttribute('answer');
		   /*
		   We know the object being returned a correctly formatted JSON string, so need 
		   To convert to a JSON object so we can get access to the values
		   */
		   var obj = JSON.parse(businessFromScriptInclude);
		   g_form.setValue('u_trading_name',obj.EntityName);
	   }catch(ex){
		   jslog(ex.message);
	   }

   }

   //Type appropriate comment here, and begin script below

}
