sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || newValue == oldValue) {
      return;
   }

   g_form.clearOptions('u_vendor_trading_name');

   var abnNumberEntered = g_form.getValue('u_abn_number');

   var checkRegFinished = false;

   checkVendorIsNotAlreadyRegistered(abnNumberEntered);

   function checkASICRecord(abn){
	   var getBusinessDetails = new GlideAjax('GetCompanyByABN');

	   getBusinessDetails.addParam('sysparm_name', 'getCompanyDetails');

	   getBusinessDetails.addParam('sysparam_ABN', abn);

	   getBusinessDetails.getXML(populateBusinessNameField);
   }


   function checkVendorIsNotAlreadyRegistered(abn){
	   var checkForABN = new GlideAjax('MasterVendorUtils');

	   checkForABN.addParam('sysparm_name', 'isAbnAlreadyRegistered');

       checkForABN.addParam('sysparam_ABNNumber', abn);

       checkForABN.getXML(checkRegistrationAnswer);
   }

   function checkRegistrationAnswer(response){
	   var registered = response.responseXML.documentElement.getAttribute('answer');
	   jslog(typeof registered);
	   if(registered == 'true'){
		   g_form.showFieldMsg('u_abn_number','The ABN entered is already an existing vendor','error');
		   return false;
	   }else{
		   checkASICRecord(abnNumberEntered);
	   }
   }

   function populateBusinessNameField(response){
	   try{
		   var businessFromScriptInclude =  response.responseXML.documentElement.getAttribute('answer');
		   /*
		   We know the object being returned a correctly formatted JSON string, so need 
		   To convert to a JSON object so we can get access to the values
		   */
		   var obj = JSON.parse(businessFromScriptInclude);
		   jslog('in vendor check for ABN service');
		   if(obj.Message == ""){
			  if(obj.AbnStatus != 'Active'){
				  g_form.showFieldMsg('u_abn_number','The ABN is not active','error');
			  } else {
				  g_form.clearValue('u_trading_name');
				  g_form.setValue('u_trading_name',obj.EntityName);
				  for(var i=0; i < obj.BusinessName.length; i++){
					  var bname = obj.BusinessName[i];
					  g_form.addOption('u_vendor_trading_name',bname, bname);
				  }
			  }
				  //g_form.setValue('u_trading_name',obj.EntityName);
		   } else {
			  jslog(JSON.stringify(obj));
			  g_form.showFieldMsg('u_abn_number',obj.Message,'error');
		   }

	   }catch(ex){
		   jslog(ex.message);
	   }

   }

   //Type appropriate comment here, and begin script below

}
