sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var termsOfPaymentRef = g_form.getReference('u_vendor_name', getVendorName);


	function getVendorName(vendor){

		try{
			var termsOfPaymentRef = vendor.payment_terms;

			var vendorId = vendor.sys_id;

			jslog('vendorId ' + vendorId);

			var getVendorPaymentTerms = new GlideAjax('GetVendorsCurrentPaymentTerms');

			var masterVendor = new GlideAjax('MasterVendorUtils');

			jslog('past the glide Ajax ' + getVendorPaymentTerms);

			masterVendor.addParam('sysparm_name', 'getVendorCurrency');

			masterVendor.addParam('sysparam_VendorID', vendorId);

			masterVendor.getXML(populateCurrency);

			getVendorPaymentTerms.addParam('sysparm_name', 'getCurrentTerms');

			getVendorPaymentTerms.addParam('sysparam_TermsRef', termsOfPaymentRef);

			getVendorPaymentTerms.getXML(populateCurrentTerms);

			jslog('past the call to the XML');

		}catch(ex){
			jslog(ex.message);
		}
	}
	function populateCurrency(response){
		var currency_code = response.responseXML.documentElement.getAttribute('answer');
		jslog(currency_code);
		g_form.setValue('u_vendor_currency_code', currency_code);
	}
	function populateCurrentTerms(response){
		var terms =  response.responseXML.documentElement.getAttribute('answer');
		jslog(terms);
		g_form.setValue('u_vendor_terms', terms);
	}

}
