sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

   if (isLoading || newValue === '' || newValue == oldValue) {
	  jslog(g_form.getValue('u_trading_name'));
      return;
   }

	g_form.clearValue('u_trading_name');
	g_form.clearValue('u_justification');
	g_form.clearValue('u_vendor_email');
	g_form.clearValue('u_vendor_contact');
	g_form.clearValue('u_describe_services');

	//reset the checkboxes
	g_form.setValue('u_engaged_vendor', false);
	g_form.setValue('u_contract_in_place_8', false);
	g_form.setValue('u_ongoing_purchasing_7', false);
	g_form.setValue('u_accept_credit_cards', false);

   //reset dropdowns
	g_form.clearOptions('u_vendor_trading_name');

}
