sys_script_include.script:
var GetVendorsCurrentPaymentTerms = Class.create();
GetVendorsCurrentPaymentTerms.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCurrentTerms: function(){

        try{
			gs.info('In the get CurrentTerms');

			var termsOfPayment = new GlideRecord('x_58872_needit_terms_of_payment');
			var termsDescription = '';

			termsOfPayment.addQuery('terms_of_payment', this.getParameter('sysparam_TermsRef'));

			termsOfPayment.query();

			gs.info('finished query');

			while(termsOfPayment.next()){
				gs.info('running through query result');
				termsDescription = termsOfPayment.terms_description;
			}
			//termsDescription = "These are the manual terms";

			return termsDescription;
		} catch(ex){
			gs.info(ex.message);
		}

	},
    type: 'GetVendorsCurrentPaymentTerms'
});
