sys_script_include.script:
var MasterVendorUtils = Class.create();
MasterVendorUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	isAbnAlreadyRegistered: function(){
		try{
			var masterVendor = new GlideRecord('x_58872_needit_ltu_master_vendor');
		    gs.info(this.getParameter('sysparam_ABNNumber'));
			recordFound = masterVendor.get('abn_number', this.getParameter('sysparam_ABNNumber'));
		    gs.info(recordFound);
			return recordFound;


		} catch(ex){
			gs.error(ex.message);
		}


	},

	isVendorNameRegistered: function(){

		try{
			var masterVendor = new GlideRecord('x_58872_needit_ltu_master_vendor');

			recordFound = masterVendor.get('legal_name', this.getParameter('sysparam_VendorName'));

			return recordFound;
		}catch(ex){
			gs.error(ex.message);
		}


	},

	getVendorCurrency: function(){
		try{
			var masterVendor = new GlideRecord('x_58872_needit_ltu_master_vendor');
			gs.info('Param passed in - ' + this.getParameter('sysparam_VendorID'));
			masterVendor.get(this.getParameter('sysparam_VendorID'));
			gs.info('currency ' + masterVendor.currency);
			return masterVendor.currency + '';
		}catch(ex){
			gs.error(ex.message);
		}

	},

    type: 'MasterVendorUtils'
});
