sys_script_include.script:
function masterVendorsWithABNOrTradingName(){
	var result = [];
	var searchOnAbn = true;
	var res = new GlideRecord('x_58872_needit_ltu_master_vendor');
	gs.info('START Of Function - ' + current.u_search_abn);

	if (current.u_search_abn){
		if (current.u_search_abn - 0){
			gs.info('Searching on ABN - ' + current.u_search_abn);
			res.addQuery('abn_number', current.u_search_abn);
		}else{
			searchOnAbn = false;
			gs.info('Searching on Trading Name - ' + current.u_search_abn);
			res.addQuery('trading_name', current.u_search_abn);
		}

	}
	res.query();
	while (res.next()) {
		if (!searchOnAbn){
			result.push(res.trading_name + '');
		}else{
			result.push(res.abn_number + '');
		}
	}
	gs.info('Results from Qry - ' + result);
	return result;
}
