var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDateTime){
		var rightnow = new GlideDateTime().getLocalDate();

		var testdate = new GlideDateTime(strDateTime).getLocalDate();

		if(testdate.compareTo(rigtnow) == -1){
			return true;
		}else{
			return false;
		}
	},

	isDateToday: function(strDateTime){
		var rightnow = new GlideDateTime().getLocalDate();

		var testdate = new GlideDateTime(strDateTime).getLocalDate();

		if(testdate.compareTo(rigtnow) == 0){
			return true;
		}else{
			return false;
		}
	},

