sys_script_include.script:
var GetCompanyByABN = Class.create();
GetCompanyByABN.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCompanyDetails: function(){

		try{
			var r = new sn_ws.RESTMessageV2('x_58872_needit.ATOABNCheck', 'Default GET');
			r.setStringParameterNoEscape('guid', gs.getProperty('x_58872_needit.atoGuid'));
			r.setStringParameterNoEscape('abn_number', this.getParameter('sysparam_ABN'));
			var response = r.execute();
			var responseBody = response.getBody();
			var responseObj = {};
			var exceptionObj = {};
			var httpStatus;


			if(responseBody.startsWith('callback')){
				/* because the RESTFul webservice call for find by ABN from ASIC returns an 
				   answer wrapped in a callback() method construct, containing the JSON payload, we
				   have to strip away the pusedo function and return the json payload. Then we 
				   to convert it to a JSON object and then serialize it to return to the caller
				*/
				var regExp = /\(([^)]+)\)/g;
				var matches = responseBody.match(regExp);
				var newBody = matches[0].substring(1,matches[0].length -1);
				//gs.info('Finished parsing body');
				//var newBody = responseBody.substring(startOfDef,endOfDef);
				responseObj = JSON.stringify(JSON.parse(newBody));
				return responseObj;

			} else {
				responseObj = JSON.parse(responseBody);
				httpStatus = response.getStatusCode();
				return responseObj;
			}


		}
		catch(ex) {
		 gs.info(ex.message); 
		}
	},

    type: 'GetCompanyByABN'
});
